import React from 'react';

const Card = ({ onClick, flipped, value, imageUrl }) => {
    return (
        <div className={`card ${flipped ? 'flipped' : ''}`} onClick={onClick}>
            <div className="card-content">
                {flipped ? <img src={value} alt="card" /> : ''}
                {imageUrl && !flipped && <img src={imageUrl} alt="card-background" className="card-background" />}
            </div>
        </div>
    );
};

export default Card;
