import React from 'react';

const Card = ({ onClick, flipped, value }) => {
    return (
        <div className={`card ${flipped ? 'flipped' : ''}`} onClick={onClick}>
            <div className="card-front">
                {flipped ? <img src={value} alt="card" /> : ''}
            </div>
            <div className="card-back"></div>
        </div>
    );
};

export default Card;
