import React from 'react';

const Card = ({ onClick, flipped, value, backgroundImage }) => {
    return (
        <div className={`card ${flipped ? 'flipped' : ''}`} onClick={onClick}>
            {flipped ? (
                <img src={value} alt="card" />
            ) : (
                <div className="card-back" style={{ backgroundImage: `url(${backgroundImage})` }}></div>
            )}
        </div>
    );
};

export default Card;
