import React from 'react';

const Card = ({ onClick, flipped, value }) => {
    return (
        <div className={`card ${flipped ? 'flipped' : ''}`} onClick={onClick}>
            {flipped ? <img src={value} alt="card" /> : <img src="/src/card-back.png" alt="card-back" />} {/* Ajout de l'élément img */}
        </div>
    );
};

export default Card;
